package java_program;

public class Table_withthefor_loops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        
		// initiliazation the DataType and variable 
		int tab=2;
		// with the help of for loop this way easily
		for(int j=1 ; j<=10; j++ ) 
		{
			// print the line by line process
		  System.out.printf(tab+"*"+j+"="+tab*j);
		  System.out.println();
		    
			
		}
	}

}
